//
//  Service Levels -- http://www.bart.gov/guide/holidays.aspx
//
//  Automatically generated by make_holiday.pl on Tue Dec 29 17:18:03 2009.
//

function Holiday () {

    servicelevel = {}
    servicelevel['12/25/2009'] = 'sunday'
    servicelevel['01/01/2010'] = 'sunday'
    servicelevel['02/15/2010'] = 'saturday'
    servicelevel['05/31/2010'] = 'sunday'
    servicelevel['09/06/2010'] = 'sunday'
    servicelevel['11/25/2010'] = 'sunday'
    servicelevel['12/25/2010'] = 'sunday'
    servicelevel['01/01/2011'] = 'sunday'
    
    Holiday.getServiceLevel = function(date) {
        var date_string = date.toMMDDYYYYString()
        var day_of_week = date.getDay()
        
        return servicelevel[date_string] ||
            ( (day_of_week == 0)        ? 'sunday'
            : (day_of_week == 6)        ? 'saturday'
            :                             'weekday')
    }
}

// Open the package.
Holiday();

