//
//  Service Levels -- http://www.bart.gov/guide/holidays.aspx
//
//  Generated by hand on 01/02/2012 since the BART holiday API returns only 2011
//  information.
//

function Holiday () {

    servicelevel = {}
    servicelevel['01/16/2012'] = 'saturday'
    servicelevel['02/20/2012'] = 'saturday'
    servicelevel['05/28/2012'] = 'sunday'
    servicelevel['07/04/2012'] = 'sunday'
    servicelevel['09/03/2012'] = 'sunday'
    servicelevel['11/22/2012'] = 'sunday'
    servicelevel['12/25/2012'] = 'sunday'
    
    Holiday.getServiceLevel = function(date) {
        var date_string = date.toMMDDYYYYString()
        var day_of_week = date.getDay()
        
        return servicelevel[date_string] ||
            ( (day_of_week == 0)        ? 'sunday'
            : (day_of_week == 6)        ? 'saturday'
            :                             'weekday')
    }
}

// Open the package.
Holiday();

